import React, {useState} from 'react'
import { 
   Button, 
   Modal, 
   ModalHeader, 
   ModalBody, 
   ModalFooter,
   Form, 
   FormGroup, 
   Label, 
   Input
} from 'reactstrap';
import {v4 as uuid} from 'uuid'
import {  useDispatch, useSelector } from 'react-redux'
import { ADD_ITEM, selectItem } from '../reducers/ItemReducers';

const ItemForm = (props) => {
   const {
     buttonLabel,
     className
   } = props;
   const itemValue = useSelector(selectItem)
   // const [nameInput, setNameInput] = useState('')
   const dispatch = useDispatch();

   const [modal, setModal] = useState(false);
   const [nameInput, setNameInput] = useState('')

   const toggle = () => setModal(!modal);
   return (
      <div /**style={{display: 'none',position: 'absolute', top: '10px', margin: '10px auto', zIndex: '5'}}*/>
         <Button color="danger" onClick={toggle}>{buttonLabel}</Button>
         <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Shopping Form</ModalHeader>
            <ModalBody>
               <Form onSubmit={onSubmit} >
                  <FormGroup>
                     <Label for='item'>Item</Label>
                  </FormGroup>
                  <input 
                     type='text'
                     name='nameInput'
                     id='item'
                     placeholder='enter your shopping item'
                     style={{position: 'absolute', top: '10px', margin: '10px auto', zIndex: '5'  }} 
                     value={nameInput} 
                     onChange={ (e)=> setNameInput(e.target.value)} 
                  />
                  
                  <span></span>
               </Form>
            </ModalBody>
            <ModalFooter>
               <Button color="primary" onClick={toggle}>Do Something</Button>
            </ModalFooter>
         </Modal>
      </div>
   )
}

export default ItemForm
