import React, {useState} from 'react'

const ItemForm = (props) => {
   const [nameInput, setNameInput] = useState('')
   return (
      <div style={{display: 'none',position: 'absolute', top: '10px', margin: '10px auto', zIndex: '5'}}>
         <input 
            style={{position: 'absolute', top: '10px', margin: '10px auto', zIndex: '5'  }} 
            value={nameInput} 
            onChange={ (e)=> setNameInput(e.target.value)} 
         />
         <span></span>
      </div>
   )
}

export default ItemForm
