import React, {useState} from 'react'
import { 
   Button, 
   Modal, 
   ModalHeader, 
   ModalBody, 
   ModalFooter,
   Form, 
   FormGroup, 
   Label, 
   Input
} from 'reactstrap';
import {v4 as uuid} from 'uuid'
import {  useDispatch, useSelector } from 'react-redux'
import { ADD_ITEM, selectItem } from '../reducers/ItemReducers';

const ItemForm = (props) => {
   const {
     buttonLabel,
     className
   } = props;
   const itemValue = useSelector(selectItem)
   // const [nameInput, setNameInput] = useState('')
   const dispatch = useDispatch();

   const [modal, setModal] = useState(false);
   const [nameInput, setNameInput] = useState('')
   const onSubmit =()=>{

   }

   const toggle = () => setModal(!modal);
   return (
      <div /**style={{display: 'none',position: 'absolute', top: '10px', margin: '10px auto', zIndex: '5'}}*/>
         <Button color="dark" 
            style={{marginBottom: '2rem'}}
            onClick={toggle}>Add Item</Button>
         <Modal isOpen={modal} toggle={toggle} className={className}>
            <ModalHeader toggle={toggle}>Shopping Form</ModalHeader>
            <ModalBody>
               <Form onSubmit={onSubmit} >
                  <FormGroup>
                     <Label for='item'>Item</Label>
                     <Input 
                        type='text'
                        name='nameInput'
                        id='item'
                        placeholder='enter your shopping item' 
                        value={nameInput} 
                        onChange={ (e)=> setNameInput(e.target.value)} 
                     />
                     <Button color='dark' style={{marginTop: '2rem'}}>Add Item</Button>
                  </FormGroup>
               </Form>
            </ModalBody>
            {/* <ModalFooter>
               <Button color="primary" onClick={toggle}>Do Something</Button>
            </ModalFooter> */}
         </Modal>
      </div>
   )
}

export default ItemForm
